(()=>{"use strict";const e=new class{constructor(){this.list=[]}addItem(e,t){const n={name:e,score:t};this.list.push(n)}};document.getElementById("submit").addEventListener("click",(()=>{const t=document.getElementById("new-name").value,n=document.getElementById("new-score").value;e.addItem(t,n),(e=>{const t=document.querySelector(".scores-container");for(;t.firstChild;)t.removeChild(t.firstChild);e.forEach((e=>{const n=document.createElement("li");n.textContent=`${e.name}: ${e.score}`,t.appendChild(n)}))})(e.list)}))})();